import { useEffect } from 'react';
import { Bread } from './Bread';
import { MainNav } from './MainNav';
import { MainSection1 } from './Mainsection1';
import { MainSection2 } from './Mainsection2';
import { MainSection3 } from './Mainsection3';
import { MainSection4 } from './Mainsection4';
import { MainSection5 } from './Mainsection5';

export function MainContent({mainsection}){

    useEffect(() => {
      // 기존 스타일 시트가 있는지 확인하고 제거
      const existingLink = document.getElementById('dynamic-stylesheet');
      if (existingLink) {
        document.head.removeChild(existingLink);
      }
  
      // 새로운 스타일 시트 추가
      const link = document.createElement('link');
      link.rel = 'stylesheet';
      link.id = 'dynamic-stylesheet';
  
      switch (mainsection) {
        case 1:
          link.href = './style/Mainsection1.css';
          break;
        case 2:
          link.href = './style/Mainsection2.css';
          break;
        case 3:
          link.href = './style/Mainsection3.css';
          break;
        case 4:
          link.href = './style/Mainsection4.css';
          break;
        case 5:
          link.href = './style/Mainsection5.css';
          break;
        default:
          link.href = ''; // No CSS
      }
  
      document.head.appendChild(link);
  
      // 컴포넌트 언마운트 시 기존 링크 제거
      return () => {
        const existingLink = document.getElementById('dynamic-stylesheet');
        if (existingLink) {
          document.head.removeChild(existingLink);
        }
      };
    }, [mainsection]);
   const mainsectionpage=()=>{
    switch(mainsection){
      case 1: 
        return <MainSection1/>
      case 2:
        return <MainSection2/>
      case 3:
        return <MainSection3/>
      case 4:
        return <MainSection4/>
      case 5:
        return <MainSection5/>

        default:
          break;
    }
   }

  return(
    <div className={`main-content ${getClassName()}`}>
      <MainNav/>
      <Bread/>
      {mainsectionpage()}
      {/* <MainSection/> */}
  </div>
  );
}
