import { Bread } from './Bread';
import { MainNav } from './MainNav';
import { MainSection1 } from './Mainsection1';
import { MainSection2 } from './Mainsection2';
import { MainSection3 } from './Mainsection3';
import { MainSection4 } from './Mainsection4';
import { MainSection5 } from './Mainsection5';
import { useEffect } from 'react';
export function MainContent({mainsection}){

  useEffect(() => {
    const link = document.createElement('link');
    link.rel = 'stylesheet';
    link.id = 'dynamic-stylesheet';

    switch (mainsection) {
      case 1:
        link.href = './project02/style/Mainsection1.css';
        break;
      case 2:
        link.href = './project02/style/Mainsection2.css';
        break;
      case 3:
        link.href = './project02/style/Mainsection3.css';
        break;
      case 4:
        link.href = './project02/style/MainSection4.css';
        break;
      case 5:
        link.href = './project02/style/MainSection5.css';
        break;
      default:
        link.href = ''; // No CSS
    }

    const existingLink = document.getElementById('dynamic-stylesheet');
    if (existingLink) {
      document.head.removeChild(existingLink);
    }
    document.head.appendChild(link);

    return () => {
      if (existingLink) {
        document.head.removeChild(existingLink);
      }
    };
  }, [mainsection]);
   const mainsectionpage=()=>{
    switch(mainsection){
      case 1: 
        return <MainSection1/>
      case 2: 
        return <MainSection2/>
      case 3:
        return <MainSection3/>
      case 4:
        return <MainSection4/>
      case 5:
        return <MainSection5/>

        default:
          break;
    }
   }

  return(
    <div className="main-content">
      <MainNav/>
      <Bread/>
      {mainsectionpage()}
      {/* <MainSection/> */}
  </div>
  );
}
