import { Bread } from './Bread';
import { MainNav } from './MainNav';
import { MainSection1 } from './Mainsection1';
import { MainSection2 } from './Mainsection2';
import { MainSection3 } from './Mainsection3';
import { MainSection4 } from './Mainsection4';
import { MainSection5 } from './Mainsection5';
import { useEffect } from 'react';

// CSS 파일 경로를 상수로 정의
const STYLES = {
  1: './project02/style/Mainsection1.css',
  2: './project02/style/Mainsection2.css',
  3: './project02/style/Mainsection3.css',
  4: './project02/style/Mainsection4.css',
  5: './project02/style/Mainsection5.css',
};

export function MainContent({ mainsection }) {
  useEffect(() => {
    const link = document.createElement('link');
    link.rel = 'stylesheet';
    link.id = 'dynamic-stylesheet';

    link.href = STYLES[mainsection] || ''; // 기본 CSS 파일 경로 설정

    const existingLink = document.getElementById('dynamic-stylesheet');
    if (existingLink) {
      document.head.removeChild(existingLink);
    }
    document.head.appendChild(link);

    return () => {
      if (existingLink) {
        document.head.removeChild(existingLink);
      }
    };
  }, [mainsection]);

  const mainsectionpage = () => {
    switch (mainsection) {
      case 1:
        return <MainSection1 />;
      case 2:
        return <MainSection2 />;
      case 3:
        return <MainSection3 />;
      case 4:
        return <MainSection4 />;
      case 5:
        return <MainSection5 />;
      default:
        return <div>Section not found</div>; // default 케이스에서 처리
    }
  };

  return (
    <div className="main-content">
      <MainNav />
      <Bread />
      {mainsectionpage()}
    </div>
  );
}
